package prat;

public class Addition {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        int x=1;
        // i want output 
        // 1
        // 2
        // 2
        System.out.println(x); // display the value of x as 1
        System.out.println(++x); // first increments the value of x and then display it as 2 
        System.out.println(x); // display the value of x, which is already incremented i.e 2
	}

}
